    createDetailsImg = () => {
        let detailImgs = [];

        for (let j = 0; j < this.mapArr3.length; j++) {
            detailImgs.push(<DetailsSlider key={this.mapArr3[j].url1} {...this.mapArr3[j]}/>);
        }
        return detailImgs;
    };



    componentDidMount() {
        this.refreshList();
    }

    refreshList = () => {
        const {stuff} = this.props.match.params;
        axios
