import React, {Component} from 'react';
import {Fade} from 'react-slideshow-image';
import '../css/pages/slider.css';
import {Link} from 'react-router-dom';

const fadeImages = [
    require('../assets/images/deliver.png'),
    require('../assets/images/fish_masala5.jpg'),
    require('../assets/images/Chicken-Masala2.1.jpg')
];

const fadeProperties = {
    duration: 200000,
    transitionDuration: 1000,
    infinite: true,
    indicators: false
}

class Slideshow extends Component {
    render() {
        return (
            <div className="Slider">
                <Fade {...fadeProperties}>
                    <div className="each-fade">
                        <div className="w3-row">
                            <div className="w3-col s6 m6 l6">
                                <div className="image-container">
                                    <img className="slider-fade-img" src={this.props.img} alt={''}/>
                                </div>
                            </div>
                            <div className="w3-col s6 m6 l6">
                                <div className="image-container1">
                                    <p>
                                        <p className="firstline">{this.props.firstline}</p>
                                        <p className="secondline">{this.props.secondline}</p>
                                        <p className="lastline">{this.props.lastline}</p></p>
                                    <Link to={'/dev/products'}>
                                        <button className="home-slider-button">shop now</button>
                                    </Link>
                                </div>
                            </div>
                        </div>
                        {/*<h2>First Slide</h2>*/}
                    </div>

                </Fade>
            </div>
        );
    }
}

export default Slideshow;
