import React, {Component} from 'react';

import '../css/pages/slider.css';
import {Link} from "react-router-dom";
// import {Fade} from "react-slideshow-image";

const fadeImages = [
    require('../assets/images/deliver.png'),
    require('../assets/images/fish_masala5.jpg'),
    require('../assets/images/Chicken-Masala2.1.jpg')
];


class Slide extends Component {

    constructor(props) {
        super(props);
    }

    slide1 = {
        img: require('../assets/images/deliver.png'),
        firstline: 'Our fleet on-demand ensures',
        secondline: 'a 2 hour delivery on all your orders',
        lastline: 'You also have 24 hours to return any items'
    };
    slide2 = {
        img: require('../assets/images/fish_masala3.jpg'),
        firstline: 'Our fleet on-demand ensures',
        secondline: 'a 2 hour delivery on all your orders',
        lastline: 'You also have 24 hours to return any items'
    };


    arr = [this.slide1, this.slide2];

    slide = this.arr.map((num) => num);


    render() {
        return (
            <div className="each-fade">
                <div className="w3-row">
                    <div className="w3-col s6 m6 l6">
                        <div className="image-container">
                            <img className="slider-fade-img" src={this.slide.img} alt={''}/>
                        </div>
                    </div>
                    <div className="w3-col s6 m6 l6">
                        <div className="image-container1">
                            <p>
                                <p className="firstline">{this.slide.firstline}</p>
                                <p className="secondline">{this.slide.secondline}</p>
                                <p className="lastline">{this.slide.lastline}</p></p>
                            <Link to={'/dev/products'}>
                                <button className="home-slider-button">shop now</button>
                            </Link>
                        </div>
                    </div>
                </div>
                {/*<h2>First Slide</h2>*/}
            </div>
        );
    }
}

export default Slide;
