import React, {Component} from 'react';
import '../css/pages/home1.css';
import Slideshow from '../components/slider';
import Categories from '../components/categories_component';
import ReactCSSTransitionGroup from 'react-addons-css-transition-group';

class Home1 extends Component {

    render() {
        let page = this.props.location.pathname.substr(1);
        if (!page) page = 'home';
        return (
            <div className="Home1">
                <Slideshow/>

                <div>
                    <h2 className="home-header-categories">iXpressMarket <strong>Categories</strong></h2>
                    <ReactCSSTransitionGroup
                        transitionName="background"
                        transitionEnterTimeout={1000}
                        transitionLeaveTimeout={1000}
                    >
                        <BackgroundImage page={page} key={page}/>
                    </ReactCSSTransitionGroup>
                </div>
            </div>
        );
    }


}

class BackgroundImage extends React.Component {
    render() {
        const urls = {
            home: 'http://i.imgur.com/kJXRAZH.jpg',
            about: 'http://i.imgur.com/TaA1gj9.png'
        };
        const style = {
            position: 'fixed',
            top: 0,
            zIndex: -1000,
            backgroundColor: '#FFFEF4',
            width: '100%'
        };
        let src = urls[this.props.page];

        return <img src={src} style={style} />;
    }
}

export default Home1;
