                        </div>
                        <a className="add-to-cart" href={item.image}>ADD TO CART</a>
                    </div>
                </div>
            </div>


        ));
    };


    render() {
        return (
            <div>

                <Header/>
                <div className="Products-page">

                    <div className="product-title">

                        <p>FRUITS</p>

                    </div>

                    <div className="container">
                        <div className="row">
                            {this.renderItems()}
                        </div>
                    </div>
                </div>
                <Footer/>
            </div>
        )
    }
}

export default Product;
