import React from 'react';
import Dropzone from 'react-dropzone';
import request from 'superagent';
// import './App.css';
import classNames from "classnames";
import download from 'image-downloader';

const CLOUDINARY_UPLOAD_PRESET = 'obnakovv';
const CLOUDINARY_UPLOAD_URL = 'https://api.cloudinary.com/v1_1/engineersticity/image/upload';

// const path = Path.resolve(--__dirname,'../assets/images',)

class ImgUpload extends React.Component {
    constructor(props) {
        super(props);

        this.state = {
            uploadedFile: null,
            uploadedFileCloudinaryUrl: ''
        };
    }

    onImageDrop = (files) => {
        this.setState({
            uploadedFile: files[0]
        });

        this.handleImageUpload(files[0]);
    };

    handleImageUpload = (file) => {
        let upload = request.post(CLOUDINARY_UPLOAD_URL)
            .field('upload_preset', CLOUDINARY_UPLOAD_PRESET)
            .field('file', file);

        upload.end((err, response) => {
            if (err) {
                console.error(err);
            }

            if (response.body.secure_url !== '') {
                this.setState({
                    uploadedFileCloudinaryUrl: response.body.secure_url
                });
            }
        });
    };

    options = {
        url: this.uploadedFileCloudinaryUrl,
        dest: `../assets/images/newPic.jpg`                  // Save to /path/to/dest/image.jpg
    };

    downloadIMG = () => {
        try {
            const {filename, image} = download.image(this.options);
            console.log(filename) // => /path/to/dest/image.jpg
        } catch (e) {
            console.error(e)
        }
    };

    render() {
        return (
            <form>
                <div className="FileUpload">
                    <Dropzone
                        onDrop={this.onImageDrop.bind(this)}
                        multiple={false}
                        accept="image/*">
                        {({getRootProps, getInputProps, isDragActive}) => {
                            return (
                                <div
                                    {...getRootProps()}
                                    className={classNames("dropzone", {
                                        "dropzone--isActive": isDragActive
                                    })}
                                >
                                    <input {...getInputProps()} />
                                    {isDragActive ? (
                                        <p>Drop files here...</p>
                                    ) : (
                                        <p>
                                            Try dropping some files here, or click to select files to
                                            upload.
                                        </p>
                                    )}
                                </div>
                            );
                        }}
                    </Dropzone>
                </div>

                <div>
                    {this.state.uploadedFileCloudinaryUrl === '' ? null :
                        <div>
                            {}
                            <p>{this.state.uploadedFile.name}</p>
                            <img src={this.state.uploadedFileCloudinaryUrl} alt={''}/>
                            {/*<a href={this.state.uploadedFileCloudinaryUrl}*/}
                            {/*   download={this.state.uploadedFile.name} target='_blank'> Download to file*/}
                            {/*</a>*/}
                            {/*const myComponent = () => <Img src={this.state.uploadedFileCloudinaryUrl}/>*/}
                            {/*<img src={this.state.uploadedFileCloudinaryUrl}  alt=""/>*/}
                            <button onClick={this.downloadIMG()}>
                                <p>Click here to download the pic to the gallery</p>
                            </button>


                        </div>}
                </div>
            </form>
        )
    }
}

export default ImgUpload;
