import React, {Component} from 'react';
import '../css/pages/checkout.css';
import axios from "axios";

console.clear();

// let productList = [
//     {name: "android", price: 231, info: "product of google"},
//     {name: "iphone", price: 784, info: "product of apple"},
//     {name: "windows", price: 156, info: "product of microsoft"}
// ];

let productList = [];

let prl = axios
    .get("https://engineersticity.pythonanywhere.com/api/products/")
    .then(res => this.setState({List: res.data, uploaded: false}))
    .catch(err => console.log(err));

/* Product */
class Product extends Component {
    constructor(props) {
        super(props);
        this.state = {
            qty: 0
        };
        this.add = this.add.bind(this);
        this.subtract = this.subtract.bind(this);
        this.showInfo = this.showInfo.bind(this);
    }

    add() {
        this.setState({
            qty: this.state.qty + 1
        });
        this.props.handleTotal(this.props.price);
    }

    subtract() {
        this.setState({
            qty: this.state.qty - 1
        });
        this.props.handleTotal(-this.props.price);
    }

    showInfo() {
        this.props.handleShow(this.props.info);
    }

    render() {
        return (
            <div>
                <div className="w3-row form-group">
                    <div className="col-sm-10">
                        <h4>{this.props.name}: ${this.props.price}</h4>
                    </div>
                    <div className="col-sm-2 text-right">qty: {this.state.qty}</div>
                </div>
                <div className="w3-row btn-toolbar">
                    <div className="col-6">
                        <button className="btn btn-outline-primary" onClick={this.showInfo}>
                            show info
                        </button>
                    </div>
                    <div className="col-6 text-right">
                        <button className="btn btn-outline-primary" onClick={this.add}>
                            +1
                        </button>
                        <button className="btn btn-outline-primary" onClick={this.subtract}
                                disabled={this.state.qty < 1}>
                            -1
                        </button>
                    </div>
                </div>
                <hr/>
            </div>
        );
    }
}

/* Total */
class Total extends Component {
    constructor(props) {
        super(props);
    }

    render() {
        let total = this.props.total.toFixed(2);
        let tax = (this.props.total * 0.15).toFixed(2);
        let totalIncTax = (+total + +tax).toFixed(2);
        let mystyle = {
            borderTop: "1px solid red",
            marginTop: "10px"
        };
        return (
            <div style={{"marginTop": "30px", "backgroundColor": "#F6F6F6", "padding": "10px"}}>
                <h3 className="w3-row" style={{fontWeight: 400}}>
                    <span className="w3-col s12 m6 l6">total price:</span>
                    <span className="w3-col s12 m6 l6 text-right">${total}</span>
                </h3>
                <h3 className="w3-row" style={{fontWeight: 400}}>
                    <span className="w3-col s12 m6 l6">tax (15%):</span>
                    <span className="w3-col s12 m6 l6 text-right">${tax}</span>
                </h3>
                <h3 className="w3-row" style={mystyle}>
                    <span className="w3-col s6 m6 l6">total inc tax:</span>
                    <span className="w3-col s6 m6 l6 text-right">${totalIncTax}</span>
                </h3>

            </div>
        );
    }
}

/* ProductForm */
class ProductForm extends Component {
    submit(e) {
        e.preventDefault();
        let product = {
            name: this.refs.name.value,
            price: Number(this.refs.price.value),
            info: this.refs.info.value
        };
        console.log(this.refs.name.value, this.refs.price.value);
        this.props.handleProduct(product);
        this.refs.name.value = "";
        this.refs.price.value = 0;
        this.refs.info.value = "";
    }

    render() {
        return (
            <div></div>
            /*
            <form onSubmit={this.submit.bind(this)}>
                <h3>add new product</h3>
                <div className="w3-row">
                    <label className="w3-col s6 m6 l6  col-sm-form-label">name:</label>
                    <div className="col-sm-10">
                        <input
                            type="text"
                            className="form-control"
                            ref="name"
                            placeholder="e.g.) android"
                            required
                        />
                    </div>
                </div>

                <div className="w3-row form-group">
                    <label className="col-sm-2  col-sm-form-label">price:</label>
                    <div className="col-sm-10">
                        <input
                            type="number"
                            className="form-control"
                            ref="price"
                            placeholder="e.g.) 100"
                            required
                        />
                    </div>
                </div>

                <div className="w3-row form-group">
