import React, {Component} from 'react';
import '../css/pages/home1.css';
import '../css/pages/slider.css';
import Slide from '../components/slide';
import {Fade} from "react-slideshow-image";

// import ProductComponent from "../components/product_component";

class Home1 extends Component {

    constructor(props) {
        super(props);
    }

    slide1 = {
        img: require('../assets/images/fish_masala5.jpg'),
        firstline: 'Our fleet on-demand ensures',
        secondline: 'a render(){<strong>2 hour delivery</strong>} on all your orders',
        lastline: 'You also have 24 hours to return any items'
    };
    slide2 = {
        img: require('../assets/images/coverpage.png'),
        firstline: 'Our fleet on-demand ensures',
        secondline: 'a <strong>2 hour delivery</strong> on all your orders',
        lastline: 'You also have 24 hours to return any items'
    };

    arr = [this.slide1, this.slide2];
    mapSlide = this.arr.map((num) => num);

    createSlides = () => {
        let slides = [];

        for (let i = 0; i < this.mapSlide.length; i++) {
            slides.push(<Slide/>)
        }
        return slides;
    };


    fadeProperties = {
        duration: 200000,
        transitionDuration: 1000,
        infinite: true,
        indicators: false
    };

    render() {

        return (
            <div className="Home1">

                <div className="Slider">
                    <Fade {...this.fadeProperties}>
                        {this.createSlides()}
                    </Fade>
                </div>


                <div>
                    <h2 className="home-header-categories">iXpressMarket <strong>Categories</strong></h2>
                </div>
            </div>
        );
    }


}

export default Home1;
